mapper.update:
UPDATE BOARD 
SET BOARD_TITLE=#{boardTitle},
	BOARD_COMMENT=#{boardComment} 
WHERE BOARD_TYPE = #{boardType} 
AND BOARD_NUM = #{boardNum}

mapper.delete:
delete 
from board 
where BOARD_TYPE = #{boardType} 
AND BOARD_NUM = #{boardNum}

mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,CODE_TYPE ,CODE_ID ,CODE_NAME ,CREATOR ,CREATE_TIME ,MODIFIER ,MODIFIED_TIME FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,B.CREATOR ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,C.CODE_TYPE ,C.CODE_ID ,C.CODE_NAME ,C.CREATE_TIME ,C.MODIFIER ,C.MODIFIED_TIME FROM BOARD B, COM_CODE C WHERE B.BOARD_TYPE = C.CODE_ID
AND (
B.BOARD_TYPE = #{o} OR
)
) WHERE NUMROW BETWEEN (#{cri.pageNum}-1)*#{cri.amount} + 1 AND (#{cri.pageNum})*#{cri.amount} AND CREATE_TIME DESC

insert.foreach:

INTO BOARD (
BOARD_TYPE
,BOARD_NUM
,BOARD_TITLE
,BOARD_COMMENT
,CREATOR
,CREATE_TIME
,MODIFIER
,MODIFIED_TIME
) 
VALUES (#{item.boardType}
,NVL(
(SELECT TO_NUMBER(MAX(BOARD_NUM)+1) FROM BOARD WHERE BOARD_TYPE = #{item.boardType}), 
,1)
,#{item.boardTitle}
,#{item.boardComment}
,#{item.creator}
,TO_CHAR(SYSDATE)
,#{item.creator}
,TO_CHAR(SYSDATE)
)
